import React from "react";
import PropTypes from "prop-types";
import { Card, Icon } from "@mui/material";
import SoftBox from "components/SoftBox";
import SoftTypography from "@mui/material/Typography";

const MessageCard = ({ image, desc, title }) => {
  return (
    <Card sx={{ display: "flex", alignItems: "center" }}>
      <SoftBox component="img" src={image} alt={title} width="100%" borderRadius="lg" />
      <SoftBox display="flex" flexDirection="column" height="100%" py={2} px={2} borderRadius="lg">
        <SoftTypography variant="h5" color="dark" fontWeight="medium">
          {desc}
        </SoftTypography>
        <SoftTypography
          component="a"
          href="https://estadisticasbcra.com/"
          variant="button"
          color="white"
          fontWeight="medium"
          sx={{
            mt: "auto",
            mr: "auto",
            display: "inline-flex",
            alignItems: "center",
            cursor: "pointer",
            "& .material-icons-round": {
              fontSize: "1.125rem",
              transform: `translate(2px, -0.5px)`,
              transition: "transform 0.2s cubic-bezier(0.34,1.61,0.7,1.3)",
            },
            "&:hover .material-icons-round, &:focus  .material-icons-round": {
              transform: `translate(6px, -0.5px)`,
            },
          }}
        >
          Read More
          <Icon sx={{ fontWeight: "bold" }}>arrow_forward</Icon>
        </SoftTypography>
      </SoftBox>
    </Card>
  );
};

MessageCard.propTypes = {
  image: PropTypes.string.isRequired,
  desc: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
};

export default MessageCard;
