// @mui material components
import Grid from "@mui/material/Grid";
import Icon from "@mui/material/Icon";
import { Box, Card } from "@mui/material";
import { useEffect, useRef, useState } from "react";

// Soft UI Dashboard React components
import SoftBox from "components/SoftBox";
import SoftTypography from "components/SoftTypography";
import SoftInput from "components/SoftInput";
import SoftButton from "components/SoftButton";

// Soft UI Dashboard React examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import MiniStatisticsCard from "examples/Cards/StatisticsCards/MiniStatisticsCard";

// Dashboard layout components
import WorkWithTheRockets from "layouts/dashboard/components/WorkWithTheRockets";

// Data
import useInflacion from "hooks/useInflacion";
import Results from "./components/Results";

//utils
import { convertDate } from "../../utils/convertDate";

const Comparador = () => {
  const [contado, setContado] = useState(undefined);
  const [cuotas, setCuotas] = useState(undefined);
  const [inflacion, setInflacion] = useState(undefined);
  const [cantidadCuotas, setCantidadCuotas] = useState(undefined);
  const [showResults, setShowResults] = useState(false);
  const [currentColor, setCurrentColor] = useState("#3acaeb"); // Initial color

  const resultsRef = useRef(null); // Referencia al elemento con el id "results"

  useEffect(() => {
    if (showResults) {
      resultsRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [showResults]);

  const { inflacionMensual, inflacionAnual } = useInflacion();

  useEffect(() => {
    inflacionMensual.fecha &&
      console.log({ inflacionMensual: convertDate(inflacionMensual.fecha) });
  }, [inflacionMensual, inflacionAnual]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentColor((prevColor) => (prevColor === "#3acaeb" ? "#17c1e8" : "#3acaeb")); // Toggle between colors
    }, 2000);

    return () => {
      clearInterval(interval); // Clear the interval when component unmounts
    };
  }, []);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <SoftBox py={3}>
        <Grid container spacing={5}>
          <Grid item xs={12} md={6} lg={7}>
            <Card>
              <SoftBox display="flex" justifyContent="space-between" alignItems="center" p={3}>
                <SoftBox>
                  <SoftTypography variant="h4" gutterBottom>
                    ¿Cuotas o Contado?
                  </SoftTypography>
                  <SoftBox display="flex" gap={1} alignItems="center" lineHeight={0}>
                    <Icon
                      sx={{
                        fontWeight: "bold",
                        color: currentColor,
                        transition: "color 0.3s ease-in-out",
                      }}
                      fontSize="large"
                    >
                      price_change
                    </Icon>
                    <SoftTypography variant="body2" fontWeight="regular" color="text">
                      <strong> Mejora tu estrategia de pagos: </strong>calcula si te conviene cuotas
                      o pago único.
                    </SoftTypography>
                  </SoftBox>
                </SoftBox>
                <SoftBox color="text" px={2}>
                  <Icon sx={{ cursor: "pointer", fontWeight: "bold" }} fontSize="medium">
                    restart_alt
                  </Icon>
                </SoftBox>
              </SoftBox>

              <SoftBox
                component="form"
                role="form"
                display="flex"
                flexDirection="column"
                justifyContent="space-around"
                gap={2.6}
                pl={2}
                pr={2}
                pb={2}
              >
                <SoftBox>
                  <SoftBox pl={0.5}>
                    <SoftTypography variant="caption" fontWeight="regular" color="text">
                      Ingresá el precio de contado
                    </SoftTypography>
                  </SoftBox>
                  <SoftInput
                    placeholder="10000"
                    icon={{
                      component: "attach_money",
                      direction: "right",
                    }}
                    value={contado}
                    onChange={(e) => setContado(e.target.value)}
                  />
                </SoftBox>
                <SoftBox>
                  <SoftBox pl={0.5}>
                    <SoftTypography variant="caption" fontWeight="regular" color="text">
                      Ingresá el precio total en cuotas
                    </SoftTypography>
                  </SoftBox>
                  <SoftInput
                    placeholder="16000"
                    icon={{
                      component: "credit_card",
                      direction: "right",
                    }}
                    value={cuotas}
                    onChange={(e) => setCuotas(e.target.value)}
                  />
                </SoftBox>
                <SoftBox>
                  <SoftBox pl={0.5}>
                    <SoftTypography variant="caption" fontWeight="regular" color="text">
                      Inflación mensual estimada
                    </SoftTypography>
                  </SoftBox>

                  <SoftInput
                    placeholder="8.4"
                    icon={{
                      component: "request_quote",
                      direction: "right",
                    }}
                    value={inflacion}
                    onChange={(e) => setInflacion(e.target.value)}
                  />
                </SoftBox>
                <SoftBox>
                  <SoftBox pl={0.5}>
                    <SoftTypography variant="caption" fontWeight="regular" color="text">
                      Cantidad de cuotas
                    </SoftTypography>
                  </SoftBox>
                  <SoftInput
                    placeholder="12"
                    icon={{
                      component: "payments",
                      direction: "right",
                    }}
                    value={cantidadCuotas}
                    onChange={(e) => setCantidadCuotas(e.target.value)}
                  />
                </SoftBox>

                <SoftButton
                  variant="contained"
                  color="dark"
                  onClick={() => setShowResults(true)}
                  fullWidth
                >
                  Calcular
                </SoftButton>
              </SoftBox>
            </Card>
          </Grid>
          <Grid item xs={12} xl={5}>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="space-between"
              height="100%"
              gap={1.5}
            >
              <MiniStatisticsCard
                title={{ text: "Inflación mensual oficial" }}
                count={inflacionMensual.valor ? `${inflacionMensual.valor}%` : "8.5%"}
                bgColor="dark"
                percentage={{
                  color: "success",
                  text: inflacionMensual.fecha && `${convertDate(inflacionMensual.fecha)}`,
                }}
                icon={{ color: "info", component: "data_thresholding" }}
              />
              <MiniStatisticsCard
                title={{ text: "Inflación esperada oficial" }}
                count={inflacionAnual.valor ? `${inflacionAnual.valor}%` : "171.2%"}
                bgColor="dark"
                percentage={{
                  color: "success",
                  text: inflacionAnual.fecha && `${convertDate(inflacionAnual.fecha)}`,
                }}
                icon={{ color: "info", component: "query_stats" }}
              />
              <WorkWithTheRockets
                titulo="Actualidad Financiera"
                altura="60%"
                desc="Mejoramos tu experiencia financiera con datos actualizados del Banco Central de la República Argentina para un servicio moderno y sencillo."
              />
            </Box>
          </Grid>

        </Grid>
      </SoftBox>

      <div
        ref={resultsRef}
        id="results"
        style={{
          visibility: showResults ? "visible" : "hidden",
          transition: "all 0.5s ease-in-out",
          display: showResults ? "block" : "none",
        }}
      >
        <Results
          contado={contado}
          cuotas={cuotas}
          inflacion={inflacion}
          cantidadCuotas={cantidadCuotas}
        />
      </div>

      {/* {showResults && (
        <div ref={resultsRef} id="results">
          <Results
            contado={contado}
            cuotas={cuotas}
            inflacion={inflacion}
            cantidadCuotas={cantidadCuotas}
          />
        </div>
      )} */}
      <Footer />
    </DashboardLayout>
  );
};

export default Comparador;
