// @mui material components
import Card from "@mui/material/Card";
import Icon from "@mui/material/Icon";
import Grid from "@mui/material/Grid";
import PropTypes from "prop-types";

// Soft UI Dashboard React components
import SoftTypography from "components/SoftTypography";

// Soft UI Dashboard React examples
import TimelineItem from "examples/Timeline/TimelineItem";

const CuotasOverview = ({ cuotasVisuales }) => {
  return (
    <Card className="h-100">
      <Grid container direction="column" pt={3} px={3}>
        <SoftTypography variant="h4" fontWeight="medium">
          Cuotas que se adaptan mensualmente según la inflación acumulada:
        </SoftTypography>
      </Grid>
      <Grid container p={2} spacing={2}>
        {cuotasVisuales &&
          cuotasVisuales.map((cuota, index) => (
            <Grid item key={index} xs={12} sm={6} md={4} lg={3}>
              <TimelineItem
                color="success"
                icon="receipt_long"
                title={cuota}
                dateTime={`Cuota #${index + 1}`}
              />
            </Grid>
          ))}
      </Grid>
    </Card>
  );
}

CuotasOverview.propTypes = {
  cuotasVisuales: PropTypes.arrayOf(PropTypes.string),
};

export default CuotasOverview;
