export const obtenerSumatoriaCuotasAjustadas = (precioContado, precioTotalCuotas, inflacionMensual, cantidadCuotas) => {
  const factorAjusteMensual = 1 + (inflacionMensual / 100);
  const factorAjusteTotal = Math.pow(factorAjusteMensual, cantidadCuotas);
  const valorCuotaAjustada = precioTotalCuotas / factorAjusteTotal;
  const sumatoriaCuotasAjustadas = valorCuotaAjustada * cantidadCuotas;
  // console.log({sumatoriaCuotasAjustadas})
  return sumatoriaCuotasAjustadas;
};

export const calcularValorCuota = (totalCuotas, inflacion, cantidadDeCuotas) => {
  const factorAjusteMensual = 1 + (inflacion / 100);
  const factorAjusteTotal = Math.pow(factorAjusteMensual, cantidadDeCuotas);
  const valorCuota = totalCuotas / factorAjusteTotal;
  return valorCuota;
};

