import { Card, Grid } from "@mui/material";
import SoftBox from "components/SoftBox";
import SoftTypography from "components/SoftTypography";
import MasterCard from "examples/Cards/MasterCard";
import React, { useEffect, useState } from "react";
import OutlinedCounterCard from "examples/Cards/CounterCards/OutlinedCounterCard";
import Invoice from "layouts/billing/components/Invoice";
import DefaultInfoCard from "examples/Cards/InfoCards/DefaultInfoCard";
import PropTypes from "prop-types";
import { obtenerSumatoriaCuotasAjustadas } from "utils/calculadora";
import ResultsInfoCard from "examples/Cards/InfoCards/ResultsInfoCard";
import { calcularValorCuota } from "utils/calculadora";
import CuotasOverview from "../CuotasOverview";
import { calcularTasaRecargo } from "utils/calculadora";
// Assets
import billeteMessi from "../../assets/images/billete-messi.webp";

const Results = ({ contado, cuotas, inflacion, cantidadCuotas }) => {
  const [cuotasArray, setCuotasArray] = useState([]);
  const [sumatoria, setSumatoria] = useState(0)

  useEffect(() => {
    setCuotasArray(calcularValorCuota(cuotas, inflacion, cantidadCuotas));
  }, [cuotas, inflacion, cantidadCuotas]);

  return (
    <SoftBox py={3} id="results">
      <Grid container spacing={5} mb={3}>
        <Grid item xs={12} md={6} lg={7}>
          <SoftBox
            color="white"
            bgColor="dark"
            variant="gradient"
            borderRadius="lg"
            shadow="lg"
            opacity={1}
          >
            <SoftTypography variant="h3" align="center" padding={2} color="white">
              {contado >
              Math.floor(
                cuotasArray.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
              )
                ? "Tu mejor opciÃ³n es comprar en Cuotas ðŸ’³"
                : "Tu mejor opciÃ³n es comprar de Contado ðŸ’°"}
            </SoftTypography>
          </SoftBox>
        </Grid>
        <Grid item xs={12} xl={5}>
          <OutlinedCounterCard
            count={Math.floor(
              cuotasArray.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
            )}
            prefix="$"
            title="Sumatoria de las cuotas ajustadas a valor de hoy"
            pad={0.8}
            color="dark"
            cap={false}
          />
        </Grid>
      </Grid>
      <Grid container spacing={3} mb={3}>
        <Grid item xs={12} xl={4}>
          <Card sx={{ height: "100%" }}>
            <SoftBox
              pt={1}
              px={2}
              display="flex"
              justifyContent="space-between"
              alignItems="center"
            >
              <SoftTypography variant="h5" fontWeight="bold">
                Transaccion
              </SoftTypography>
              {/* <SoftButton variant="outlined" color="info" size="small">
                view all
              </SoftButton> */}
            </SoftBox>
            <SoftBox p={2}>
              <SoftBox component="ul" display="flex" flexDirection="column" p={0} m={0}>
                <Invoice date="Precio de contado" price={`$ ${contado}`} />
                <Invoice date="Precio total en cuotas" price={`$ ${cuotas}`} />
                <Invoice date="InflaciÃ³n mensual estimada" price={`${inflacion}%`} />
                <Invoice date="Cantidad de cuotas" price={cantidadCuotas} noGutter />
              </SoftBox>
            </SoftBox>
          </Card>
        </Grid>
        <Grid item xs={12} xl={4}>
          <MasterCard number={4562112245947852} holder="Leo Messi" expires="11/27" color="dark" />
        </Grid>
        <Grid item xs={12} md={6} xl={2}>
          <ResultsInfoCard
            icon="account_balance"
            title="Tasa de recargo"
            description="Ajustando el costo con cuotas e inflaciÃ³n."
            value={`${calcularTasaRecargo(contado, cuotas)}%`}
          />
        </Grid>
        <Grid item xs={12} md={6} xl={2}>
          <ResultsInfoCard
            icon="payment"
            title="Cuota mensual"
            description="SuscripciÃ³n financiera, estilo moderno."
            value={`$${Math.round(cuotas / cantidadCuotas)}`}
          />
        </Grid>
      </Grid>
      <Grid container spacing={3} mb={3}>
        <Grid item xs={12} md={6} xl={16}>
          <CuotasOverview cuotasVisuales={cuotasArray} />
        </Grid>
      </Grid>
    </SoftBox>
  );
};
Results.propTypes = {
  decision: PropTypes.string.isRequired,
  contado: PropTypes.number.isRequired,
  cuotas: PropTypes.number.isRequired,
  inflacion: PropTypes.number.isRequired,
  cantidadCuotas: PropTypes.number.isRequired,
};

export default Results;
